function depots_insert() {
	global $Translation;

	// mm: can member insert record?
	$arrPerm = getTablePermissions('depots');
	if(!$arrPerm[1]) return false;

	$data = array();
	$data['sur_le_site'] = $_REQUEST['sur_le_site'];
		if($data['sur_le_site'] == empty_lookup_value) { $data['sur_le_site'] = ''; }
	$data['dans_le_bac'] = $_REQUEST['dans_le_bac'];
		if($data['dans_le_bac'] == empty_lookup_value) { $data['dans_le_bac'] = ''; }
	$data['jour_heure'] = mysql_datetime($_REQUEST['jour_heure']);
		if($data['jour_heure'] == empty_lookup_value) { $data['jour_heure'] = ''; }
	$data['qte_vmh'] = $_REQUEST['qte_vmh'];
		if($data['qte_vmh'] == empty_lookup_value) { $data['qte_vmh'] = ''; }
	$data['qte_bds'] = $_REQUEST['qte_bds'];
		if($data['qte_bds'] == empty_lookup_value) { $data['qte_bds'] = ''; }
	$data['niveau_du_bac'] = $_REQUEST['niveau_du_bac'];
		if($data['niveau_du_bac'] == empty_lookup_value) { $data['niveau_du_bac'] = ''; }
	$data['cree_par'] = parseCode('<%%creatorUsername%%>', true);

	// hook: depots_before_insert
	if(function_exists('depots_before_insert')) {
		$args = array();
		if(!depots_before_insert($data, getMemberInfo(), $args)) { return false; }
	}

	$error = '';
	// set empty fields to NULL
	$data = array_map(function($v) { return ($v === '' ? NULL : $v); }, $data);
	insert('depots', backtick_keys_once($data), $error);
	if($error)
		die("{$error}<br><a href=\"#\" onclick=\"history.go(-1);\">{$Translation['< back']}</a>");

	$recID = db_insert_id(db_link());

	// automatic par_le_membre if passed as filterer
	if($_REQUEST['filterer_par_le_membre']) {
		sql("update `depots` set `par_le_membre`='" . makeSafe($_REQUEST['filterer_par_le_membre']) . "' where `id`='" . makeSafe($recID, false) . "'", $eo);
	}

	// hook: depots_after_insert
	if(function_exists('depots_after_insert')) {
		$res = sql("select * from `depots` where `id`='" . makeSafe($recID, false) . "' limit 1", $eo);
		if($row = db_fetch_assoc($res)) {
			$data = array_map('makeSafe', $row);
		}
		$data['selectedID'] = makeSafe($recID, false);
		$args=array();
		if(!depots_after_insert($data, getMemberInfo(), $args)) { return $recID; }
	}

	// mm: save ownership data
	set_record_owner('depots', $recID, getLoggedMemberID());

	// if this record is a copy of another record, copy children if applicable
	if(!empty($_REQUEST['SelectedID'])) depots_copy_children($recID, $_REQUEST['SelectedID']);

	return $recID;
}

