	global $Translation;
	$selected_id=makeSafe($selected_id);

	// mm: can member delete record?
	$arrPerm=getTablePermissions('depots');
	$ownerGroupID=sqlValue("select groupID from membership_userrecords where tableName='depots' and pkValue='$selected_id'");
	$ownerMemberID=sqlValue("select lcase(memberID) from membership_userrecords where tableName='depots' and pkValue='$selected_id'");
	if(($arrPerm[4]==1 && $ownerMemberID==getLoggedMemberID()) || ($arrPerm[4]==2 && $ownerGroupID==getLoggedGroupID()) || $arrPerm[4]==3) { // allow delete?
		// delete allowed, so continue ...
	}else{
		return $Translation['You don\'t have enough permissions to delete this record'];
	}

	// hook: depots_before_delete
	if(function_exists('depots_before_delete')) {
		$args=array();
		if(!depots_before_delete($selected_id, $skipChecks, getMemberInfo(), $args))
			return $Translation['Couldn\'t delete this record'];
	}

	sql("delete from `depots` where `id`='$selected_id'", $eo);

	// hook: depots_after_delete
	if(function_exists('depots_after_delete')) {
		$args=array();
		depots_after_delete($selected_id, getMemberInfo(), $args);
	}

	// mm: delete ownership data
	sql("delete from membership_userrecords where tableName='depots' and pkValue='$selected_id'", $eo);
}

