		$args = array();
		if(!depots_before_update($data, getMemberInfo(), $args)) { return false; }
	}

	$o = array('silentErrors' => true);
	sql('update `depots` set       `sur_le_site`=' . (($data['sur_le_site'] !== '' && $data['sur_le_site'] !== NULL) ? "'{$data['sur_le_site']}'" : 'NULL') . ', `dans_le_bac`=' . (($data['dans_le_bac'] !== '' && $data['dans_le_bac'] !== NULL) ? "'{$data['dans_le_bac']}'" : 'NULL') . ', `jour_heure`=' . (($data['jour_heure'] !== '' && $data['jour_heure'] !== NULL) ? "'{$data['jour_heure']}'" : 'NULL') . ', `qte_vmh`=' . (($data['qte_vmh'] !== '' && $data['qte_vmh'] !== NULL) ? "'{$data['qte_vmh']}'" : 'NULL') . ', `qte_bds`=' . (($data['qte_bds'] !== '' && $data['qte_bds'] !== NULL) ? "'{$data['qte_bds']}'" : 'NULL') . ', `niveau_du_bac`=' . (($data['niveau_du_bac'] !== '' && $data['niveau_du_bac'] !== NULL) ? "'{$data['niveau_du_bac']}'" : 'NULL') . ', `date_maj`=' . "'{$data['date_maj']}'" . " where `id`='".makeSafe($selected_id)."'", $o);
	if($o['error']!='') {
		echo $o['error'];
		echo '<a href="depots_view.php?SelectedID='.urlencode($selected_id)."\">{$Translation['< back']}</a>";
		exit;
	}


	// hook: depots_after_update
	if(function_exists('depots_after_update')) {
		$res = sql("SELECT * FROM `depots` WHERE `id`='{$data['selectedID']}' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)) {
			$data = array_map('makeSafe', $row);
		}
		$data['selectedID'] = $data['id'];
		$args = array();
		if(!depots_after_update($data, getMemberInfo(), $args)) { return; }
	}

	// mm: update ownership data
	sql("update `membership_userrecords` set `dateUpdated`='" . time() . "' where `tableName`='depots' and `pkValue`='" . makeSafe($selected_id) . "'", $eo);

}

