	$rdata = $jdata = get_defaults('depots');
	if($selected_id) {
		$jdata = get_joined_record('depots', $selected_id);
		if($jdata === false) $jdata = get_defaults('depots');
		$rdata = $row;
	}
	$templateCode .= loadView('depots-ajax-cache', array('rdata' => $rdata, 'jdata' => $jdata));

	// hook: depots_dv
	if(function_exists('depots_dv')) {
		$args=array();
		depots_dv(($selected_id ? $selected_id : FALSE), getMemberInfo(), $templateCode, $args);
	}

	return $templateCode;
}
?>