function membres_insert() {
	global $Translation;

	// mm: can member insert record?
	$arrPerm = getTablePermissions('membres');
	if(!$arrPerm[1]) return false;

	$data = array();
	$data['Nom'] = $_REQUEST['Nom'];
		if($data['Nom'] == empty_lookup_value) { $data['Nom'] = ''; }
	$data['prenom'] = $_REQUEST['prenom'];
		if($data['prenom'] == empty_lookup_value) { $data['prenom'] = ''; }
	$data['statut'] = $_REQUEST['statut'];
		if($data['statut'] == empty_lookup_value) { $data['statut'] = ''; }
	$data['adresse_du_membre'] = br2nl($_REQUEST['adresse_du_membre']);
	$data['email_membre'] = $_REQUEST['email_membre'];
		if($data['email_membre'] == empty_lookup_value) { $data['email_membre'] = ''; }
	$data['Telephone_membre'] = $_REQUEST['Telephone_membre'];
		if($data['Telephone_membre'] == empty_lookup_value) { $data['Telephone_membre'] = ''; }
	if($data['statut'] == '') $data['statut'] = "habitant";

	// hook: membres_before_insert
	if(function_exists('membres_before_insert')) {
		$args = array();
		if(!membres_before_insert($data, getMemberInfo(), $args)) { return false; }
	}

	$error = '';
	// set empty fields to NULL
	$data = array_map(function($v) { return ($v === '' ? NULL : $v); }, $data);
	insert('membres', backtick_keys_once($data), $error);
	if($error)
		die("{$error}<br><a href=\"#\" onclick=\"history.go(-1);\">{$Translation['< back']}</a>");

	$recID = db_insert_id(db_link());

	// hook: membres_after_insert
	if(function_exists('membres_after_insert')) {
		$res = sql("select * from `membres` where `id`='" . makeSafe($recID, false) . "' limit 1", $eo);
		if($row = db_fetch_assoc($res)) {
			$data = array_map('makeSafe', $row);
		}
		$data['selectedID'] = makeSafe($recID, false);
		$args=array();
		if(!membres_after_insert($data, getMemberInfo(), $args)) { return $recID; }
	}

	// mm: save ownership data
	set_record_owner('membres', $recID, getLoggedMemberID());

	// if this record is a copy of another record, copy children if applicable
	if(!empty($_REQUEST['SelectedID'])) membres_copy_children($recID, $_REQUEST['SelectedID']);

	return $recID;
}

function membres_copy_children($destination_id, $source_id) {
	global $Translation;
	$requests = array(); // array of curl handlers for launching insert requests
	$eo = array('silentErrors' => true);
	$uploads_dir = realpath(dirname(__FILE__) . '/../' . $Translation['ImageFolder']);
	$safe_sid = makeSafe($source_id);

	// launch requests, asynchronously
	curl_batch($requests);
}

