function membres_update($selected_id) {
	global $Translation;

	// mm: can member edit record?
	$arrPerm=getTablePermissions('membres');
	$ownerGroupID=sqlValue("select groupID from membership_userrecords where tableName='membres' and pkValue='".makeSafe($selected_id)."'");
	$ownerMemberID=sqlValue("select lcase(memberID) from membership_userrecords where tableName='membres' and pkValue='".makeSafe($selected_id)."'");
	if(($arrPerm[3]==1 && $ownerMemberID==getLoggedMemberID()) || ($arrPerm[3]==2 && $ownerGroupID==getLoggedGroupID()) || $arrPerm[3]==3) { // allow update?
		// update allowed, so continue ...
	}else{
		return false;
	}

	$data['Nom'] = makeSafe($_REQUEST['Nom']);
		if($data['Nom'] == empty_lookup_value) { $data['Nom'] = ''; }
	$data['prenom'] = makeSafe($_REQUEST['prenom']);
		if($data['prenom'] == empty_lookup_value) { $data['prenom'] = ''; }
	$data['statut'] = makeSafe($_REQUEST['statut']);
		if($data['statut'] == empty_lookup_value) { $data['statut'] = ''; }
	$data['adresse_du_membre'] = br2nl(makeSafe($_REQUEST['adresse_du_membre']));
	$data['email_membre'] = makeSafe($_REQUEST['email_membre']);
		if($data['email_membre'] == empty_lookup_value) { $data['email_membre'] = ''; }
	$data['Telephone_membre'] = makeSafe($_REQUEST['Telephone_membre']);
		if($data['Telephone_membre'] == empty_lookup_value) { $data['Telephone_membre'] = ''; }
	$data['selectedID'] = makeSafe($selected_id);

	// hook: membres_before_update
	if(function_exists('membres_before_update')) {
		$args = array();
		if(!membres_before_update($data, getMemberInfo(), $args)) { return false; }
	}

	$o = array('silentErrors' => true);
	sql('update `membres` set       `Nom`=' . (($data['Nom'] !== '' && $data['Nom'] !== NULL) ? "'{$data['Nom']}'" : 'NULL') . ', `prenom`=' . (($data['prenom'] !== '' && $data['prenom'] !== NULL) ? "'{$data['prenom']}'" : 'NULL') . ', `statut`=' . (($data['statut'] !== '' && $data['statut'] !== NULL) ? "'{$data['statut']}'" : 'NULL') . ', `adresse_du_membre`=' . (($data['adresse_du_membre'] !== '' && $data['adresse_du_membre'] !== NULL) ? "'{$data['adresse_du_membre']}'" : 'NULL') . ', `email_membre`=' . (($data['email_membre'] !== '' && $data['email_membre'] !== NULL) ? "'{$data['email_membre']}'" : 'NULL') . ', `Telephone_membre`=' . (($data['Telephone_membre'] !== '' && $data['Telephone_membre'] !== NULL) ? "'{$data['Telephone_membre']}'" : 'NULL') . " where `id`='".makeSafe($selected_id)."'", $o);
	if($o['error']!='') {
		echo $o['error'];
		echo '<a href="membres_view.php?SelectedID='.urlencode($selected_id)."\">{$Translation['< back']}</a>";
		exit;
	}


	// hook: membres_after_update
	if(function_exists('membres_after_update')) {
		$res = sql("SELECT * FROM `membres` WHERE `id`='{$data['selectedID']}' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)) {
			$data = array_map('makeSafe', $row);
		}
		$data['selectedID'] = $data['id'];
		$args = array();
		if(!membres_after_update($data, getMemberInfo(), $args)) { return; }
	}

	// mm: update ownership data
	sql("update `membership_userrecords` set `dateUpdated`='" . time() . "' where `tableName`='membres' and `pkValue`='" . makeSafe($selected_id) . "'", $eo);

}

