		// if user has insert permission to parent table of a lookup field, put an add new button
		if($pt_perm['insert'] && !$_REQUEST['Embedded']) {
			$templateCode = str_replace("<%%ADDNEW({$ptfc[0]})%%>", '<button type="button" class="btn btn-success add_new_parent hspacer-md" id="' . $ptfc[0] . '_add_new" title="' . html_attr($Translation['Add New'] . ' ' . $ptfc[1]) . '"><i class="glyphicon glyphicon-plus-sign"></i></button>', $templateCode);
		}
	}

	// process images
	$templateCode = str_replace('<%%UPLOADFILE(id)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(Nom)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(prenom)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(statut)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(adresse_du_membre)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(email_membre)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(Telephone_membre)%%>', '', $templateCode);

	// process values
	if($selected_id) {
		if( $dvprint) $templateCode = str_replace('<%%VALUE(id)%%>', safe_html($urow['id']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(id)%%>', html_attr($row['id']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(id)%%>', urlencode($urow['id']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(Nom)%%>', safe_html($urow['Nom']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(Nom)%%>', html_attr($row['Nom']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Nom)%%>', urlencode($urow['Nom']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(prenom)%%>', safe_html($urow['prenom']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(prenom)%%>', html_attr($row['prenom']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(prenom)%%>', urlencode($urow['prenom']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(statut)%%>', safe_html($urow['statut']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(statut)%%>', html_attr($row['statut']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(statut)%%>', urlencode($urow['statut']), $templateCode);
		if($dvprint || (!$AllowUpdate && !$AllowInsert)) {
			$templateCode = str_replace('<%%VALUE(adresse_du_membre)%%>', safe_html($urow['adresse_du_membre']), $templateCode);
		}else{
			$templateCode = str_replace('<%%VALUE(adresse_du_membre)%%>', html_attr($row['adresse_du_membre']), $templateCode);
		}
		$templateCode = str_replace('<%%URLVALUE(adresse_du_membre)%%>', urlencode($urow['adresse_du_membre']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(email_membre)%%>', safe_html($urow['email_membre']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(email_membre)%%>', html_attr($row['email_membre']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(email_membre)%%>', urlencode($urow['email_membre']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(Telephone_membre)%%>', safe_html($urow['Telephone_membre']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(Telephone_membre)%%>', html_attr($row['Telephone_membre']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Telephone_membre)%%>', urlencode($urow['Telephone_membre']), $templateCode);
	}else{
		$templateCode = str_replace('<%%VALUE(id)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(id)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(Nom)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Nom)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(prenom)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(prenom)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(statut)%%>', 'habitant', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(statut)%%>', urlencode('habitant'), $templateCode);
		$templateCode = str_replace('<%%VALUE(adresse_du_membre)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(adresse_du_membre)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(email_membre)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(email_membre)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(Telephone_membre)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Telephone_membre)%%>', urlencode(''), $templateCode);
	}

	// process translations
	foreach($Translation as $symbol=>$trans) {
		$templateCode = str_replace("<%%TRANSLATION($symbol)%%>", $trans, $templateCode);
	}

	// clear scrap
	$templateCode = str_replace('<%%', '<!-- ', $templateCode);
	$templateCode = str_replace('%%>', ' -->', $templateCode);

	// hide links to inaccessible tables
	if($_REQUEST['dvprint_x'] == '') {
		$templateCode .= "\n\n<script>\$j(function() {\n";
		$arrTables = getTableList();
		foreach($arrTables as $name => $caption) {
			$templateCode .= "\t\$j('#{$name}_link').removeClass('hidden');\n";
			$templateCode .= "\t\$j('#xs_{$name}_link').removeClass('hidden');\n";
		}

		$templateCode .= $jsReadOnly;
		$templateCode .= $jsEditable;

		if(!$selected_id) {
		}

		$templateCode.="\n});</script>\n";
	}

	// ajaxed auto-fill fields
	$templateCode .= '<script>';
	$templateCode .= '$j(function() {';


	$templateCode.="});";
	$templateCode.="</script>";
	$templateCode .= $lookups;

	// handle enforced parent values for read-only lookup fields

	// don't include blank images in lightbox gallery
	$templateCode = preg_replace('/blank.gif" data-lightbox=".*?"/', 'blank.gif"', $templateCode);

	// don't display empty email links
	$templateCode=preg_replace('/<a .*?href="mailto:".*?<\/a>/', '', $templateCode);

	/* default field values */
	$rdata = $jdata = get_defaults('membres');
	if($selected_id) {
		$jdata = get_joined_record('membres', $selected_id);
		if($jdata === false) $jdata = get_defaults('membres');
		$rdata = $row;
	}
	$templateCode .= loadView('membres-ajax-cache', array('rdata' => $rdata, 'jdata' => $jdata));

	// hook: membres_dv
	if(function_exists('membres_dv')) {
		$args=array();
		membres_dv(($selected_id ? $selected_id : FALSE), getMemberInfo(), $templateCode, $args);
	}

	return $templateCode;
}
