	if($data['email_du_responsable'] == '') $data['email_du_responsable'] = "adresse@email.com";

	// hook: site_compostage_before_insert
	if(function_exists('site_compostage_before_insert')) {
		$args = array();
		if(!site_compostage_before_insert($data, getMemberInfo(), $args)) { return false; }
	}

	$error = '';
	// set empty fields to NULL
	$data = array_map(function($v) { return ($v === '' ? NULL : $v); }, $data);
	insert('site_compostage', backtick_keys_once($data), $error);
	if($error)
		die("{$error}<br><a href=\"#\" onclick=\"history.go(-1);\">{$Translation['< back']}</a>");

	$recID = db_insert_id(db_link());

	// hook: site_compostage_after_insert
	if(function_exists('site_compostage_after_insert')) {
		$res = sql("select * from `site_compostage` where `id`='" . makeSafe($recID, false) . "' limit 1", $eo);
