		$args=array();
		if(!site_compostage_after_insert($data, getMemberInfo(), $args)) { return $recID; }
	}

	// mm: save ownership data
	set_record_owner('site_compostage', $recID, getLoggedMemberID());

	// if this record is a copy of another record, copy children if applicable
	if(!empty($_REQUEST['SelectedID'])) site_compostage_copy_children($recID, $_REQUEST['SelectedID']);

	return $recID;
