		$jsReadOnly .= "\tjQuery('#acces_au_site').replaceWith('');\n";
		$jsReadOnly .= "\tjQuery('#acces_au_site, #acces_au_site-edit-link').hide();\n";
		$jsReadOnly .= "\tjQuery('#type_de_site').replaceWith('<div class=\"form-control-static\" id=\"type_de_site\">' + (jQuery('#type_de_site').val() || '') + '</div>'); jQuery('#type_de_site-multi-selection-help').hide();\n";
		$jsReadOnly .= "\tjQuery('#nom_du_responsable').replaceWith('<div class=\"form-control-static\" id=\"nom_du_responsable\">' + (jQuery('#nom_du_responsable').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('#email_du_responsable').replaceWith('<div class=\"form-control-static\" id=\"email_du_responsable\">' + (jQuery('#email_du_responsable').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('#email_du_responsable, #email_du_responsable-edit-link').hide();\n";
		$jsReadOnly .= "\tjQuery('#Telephone_du_responsable').replaceWith('<div class=\"form-control-static\" id=\"Telephone_du_responsable\">' + (jQuery('#Telephone_du_responsable').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('.select2-container').hide();\n";

		$noUploads = true;
	}elseif($AllowInsert) {
		$jsEditable .= "\tjQuery('form').eq(0).data('already_changed', true);"; // temporarily disable form change handler
			$jsEditable .= "\tjQuery('form').eq(0).data('already_changed', false);"; // re-enable form change handler
	}

	// process combos
	$templateCode = str_replace('<%%COMBO(type_de_site)%%>', $combo_type_de_site->HTML, $templateCode);
	$templateCode = str_replace('<%%COMBOTEXT(type_de_site)%%>', $combo_type_de_site->SelectedData, $templateCode);

	/* lookup fields array: 'lookup field name' => array('parent table name', 'lookup field caption') */
	$lookup_fields = array();
	foreach($lookup_fields as $luf => $ptfc) {
		$pt_perm = getTablePermissions($ptfc[0]);

		// process foreign key links
		if($pt_perm['view'] || $pt_perm['edit']) {
			$templateCode = str_replace("<%%PLINK({$luf})%%>", '<button type="button" class="btn btn-default view_parent hspacer-md" id="' . $ptfc[0] . '_view_parent" title="' . html_attr($Translation['View'] . ' ' . $ptfc[1]) . '"><i class="glyphicon glyphicon-eye-open"></i></button>', $templateCode);
		}

		// if user has insert permission to parent table of a lookup field, put an add new button
		if($pt_perm['insert'] && !$_REQUEST['Embedded']) {
			$templateCode = str_replace("<%%ADDNEW({$ptfc[0]})%%>", '<button type="button" class="btn btn-success add_new_parent hspacer-md" id="' . $ptfc[0] . '_add_new" title="' . html_attr($Translation['Add New'] . ' ' . $ptfc[1]) . '"><i class="glyphicon glyphicon-plus-sign"></i></button>', $templateCode);
		}
	}

	// process images
	$templateCode = str_replace('<%%UPLOADFILE(id)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(nom_du_site)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(adresse_postale)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(acces_au_site)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(type_de_site)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(nom_du_responsable)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(email_du_responsable)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(Telephone_du_responsable)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(Commentaires)%%>', '', $templateCode);

	// process values
	if($selected_id) {
		if( $dvprint) $templateCode = str_replace('<%%VALUE(id)%%>', safe_html($urow['id']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(id)%%>', html_attr($row['id']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(id)%%>', urlencode($urow['id']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(nom_du_site)%%>', safe_html($urow['nom_du_site']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(nom_du_site)%%>', html_attr($row['nom_du_site']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(nom_du_site)%%>', urlencode($urow['nom_du_site']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(adresse_postale)%%>', safe_html($urow['adresse_postale']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(adresse_postale)%%>', html_attr($row['adresse_postale']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(adresse_postale)%%>', urlencode($urow['adresse_postale']), $templateCode);
		$templateCode = str_replace('<%%VALUE(acces_au_site)%%>', html_attr($row['acces_au_site']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(acces_au_site)%%>', urlencode($urow['acces_au_site']), $templateCode);
		$templateCode = str_replace('<%%GOOGLEMAP(acces_au_site)%%>', get_embed('googlemap', $urow['acces_au_site'], '480', '360'), $templateCode);
		$templateCode = str_replace('<%%GOOGLEMAPTHUMB(acces_au_site)%%>', thisOr(get_embed('googlemap', $urow['acces_au_site'], '480', '360', 'thumbnail_url'), 'blank.gif'), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(type_de_site)%%>', safe_html($urow['type_de_site']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(type_de_site)%%>', html_attr($row['type_de_site']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(type_de_site)%%>', urlencode($urow['type_de_site']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(nom_du_responsable)%%>', safe_html($urow['nom_du_responsable']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(nom_du_responsable)%%>', html_attr($row['nom_du_responsable']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(nom_du_responsable)%%>', urlencode($urow['nom_du_responsable']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(email_du_responsable)%%>', safe_html($urow['email_du_responsable']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(email_du_responsable)%%>', html_attr($row['email_du_responsable']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(email_du_responsable)%%>', urlencode($urow['email_du_responsable']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(Telephone_du_responsable)%%>', safe_html($urow['Telephone_du_responsable']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(Telephone_du_responsable)%%>', html_attr($row['Telephone_du_responsable']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Telephone_du_responsable)%%>', urlencode($urow['Telephone_du_responsable']), $templateCode);
		if($AllowUpdate || $AllowInsert) {
			$templateCode = str_replace('<%%HTMLAREA(Commentaires)%%>', '<textarea name="Commentaires" id="Commentaires" rows="5">' . html_attr($row['Commentaires']) . '</textarea>', $templateCode);
		}else{
			$templateCode = str_replace('<%%HTMLAREA(Commentaires)%%>', '<div id="Commentaires" class="form-control-static">' . $row['Commentaires'] . '</div>', $templateCode);
		}
		$templateCode = str_replace('<%%VALUE(Commentaires)%%>', nl2br($row['Commentaires']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Commentaires)%%>', urlencode($urow['Commentaires']), $templateCode);
	}else{
		$templateCode = str_replace('<%%VALUE(id)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(id)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(nom_du_site)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(nom_du_site)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(adresse_postale)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(adresse_postale)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(acces_au_site)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(acces_au_site)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%GOOGLEMAP(acces_au_site)%%>', '', $templateCode);
		$templateCode = str_replace('<%%GOOGLEMAPTHUMB(acces_au_site)%%>', 'blank.gif', $templateCode);
		$templateCode = str_replace('<%%VALUE(type_de_site)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(type_de_site)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(nom_du_responsable)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(nom_du_responsable)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(email_du_responsable)%%>', 'adresse@email.com', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(email_du_responsable)%%>', urlencode('adresse@email.com'), $templateCode);
		$templateCode = str_replace('<%%VALUE(Telephone_du_responsable)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Telephone_du_responsable)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%HTMLAREA(Commentaires)%%>', '<textarea name="Commentaires" id="Commentaires" rows="5"></textarea>', $templateCode);
	}

	// process translations
	foreach($Translation as $symbol=>$trans) {
		$templateCode = str_replace("<%%TRANSLATION($symbol)%%>", $trans, $templateCode);
	}

	// clear scrap
	$templateCode = str_replace('<%%', '<!-- ', $templateCode);
	$templateCode = str_replace('%%>', ' -->', $templateCode);

	// hide links to inaccessible tables
	if($_REQUEST['dvprint_x'] == '') {
		$templateCode .= "\n\n<script>\$j(function() {\n";
		$arrTables = getTableList();
		foreach($arrTables as $name => $caption) {
			$templateCode .= "\t\$j('#{$name}_link').removeClass('hidden');\n";
			$templateCode .= "\t\$j('#xs_{$name}_link').removeClass('hidden');\n";
		}

		$templateCode .= $jsReadOnly;
		$templateCode .= $jsEditable;

		if(!$selected_id) {
			$templateCode.="\n\tif(document.getElementById('email_du_responsableEdit')) { document.getElementById('email_du_responsableEdit').style.display='inline'; }";
			$templateCode.="\n\tif(document.getElementById('email_du_responsableEditLink')) { document.getElementById('email_du_responsableEditLink').style.display='none'; }";
		}

		$templateCode.="\n});</script>\n";
	}

	// ajaxed auto-fill fields
	$templateCode .= '<script>';
	$templateCode .= '$j(function() {';


	$templateCode.="});";
	$templateCode.="</script>";
	$templateCode .= $lookups;

	// handle enforced parent values for read-only lookup fields

	// don't include blank images in lightbox gallery
	$templateCode = preg_replace('/blank.gif" data-lightbox=".*?"/', 'blank.gif"', $templateCode);

	// don't display empty email links
	$templateCode=preg_replace('/<a .*?href="mailto:".*?<\/a>/', '', $templateCode);

	/* default field values */
	$rdata = $jdata = get_defaults('site_compostage');
	if($selected_id) {
		$jdata = get_joined_record('site_compostage', $selected_id);
		if($jdata === false) $jdata = get_defaults('site_compostage');
		$rdata = $row;
	}
	$templateCode .= loadView('site_compostage-ajax-cache', array('rdata' => $rdata, 'jdata' => $jdata));

	// hook: site_compostage_dv
	if(function_exists('site_compostage_dv')) {
		$args=array();
		site_compostage_dv(($selected_id ? $selected_id : FALSE), getMemberInfo(), $templateCode, $args);
	}

	return $templateCode;
}
?>